{
  "query": "query (
  $sort: String
  $after: String
  $first: Int
  $filter: String
) {
  contentFragments(sort: $sort, after: $after, limit: $first, filter: $filter) {
    edges {
      node {
        path
        title
        status
        statusPreview
        created {
          at
          by
        }
        modified {
          at
          by
        }
        tags {               id               name               path               description               title               titlePath             }
        modifiedOrCreated {
          at
          by
        }
        published {
          at
          by
        }
        model {
          name
          path
        }
        parent {
          path
          name
          title
        }
      }
    }
    pageInfo {
      startCursor
      endCursor
      hasNextPage
      hasPreviousPage
    }
  }
}
",
  "variables": {
    "sort": "modifiedOrCreated DESC",
    "after": null,
    "first": 100,
    "filter": "path=/content/dam/wknd-shared;",
    "withStatusPreview": true,
    "withTags": true
  }
}
